mapper.select:
SELECT 
  COUNT(1) 
FROM
  `taxagency_customer` a 
WHERE
 a.`customerState` != '02' 
and a.`organizationId` = #{organizationId}
and a.`taxNumber` = #{taxNumber}
and a.`customerName` = #{customerName}
and a.`customerId` != #{customerId}

mapper.update:
UPDATE taxagency_customer
accountingUserCode = #{accountingUserCode}, organizationId = #{organizationId}, customerName = #{customerName}, customerLevel = #{customerLevel}, customerInfo = #{customerInfo}, customerImage = #{customerImage}, areaCode = #{areaCode}, customerContacts = #{customerContacts}, customerContactsPhone = #{customerContactsPhone}, taxNumber = #{taxNumber}, isSetAccount = #{isSetAccount}, customerState = #{customerState}, customerEmail = #{customerEmail}, weixinCode = #{weixinCode}, businessLicense = #{businessLicense}, legalRepre = #{legalRepre}, legalPersonID = #{legalPersonID}, addressId = #{addressId}, customerAddress = #{customerAddress}, taxAuthoritiesTypeCode = #{taxAuthoritiesTypeCode}, taxAdministrator = #{taxAdministrator}, localtaxAuthoritiesTypeCode = #{localtaxAuthoritiesTypeCode}, localTaxAdministrator = #{localTaxAdministrator}, businessTypeCode = #{businessTypeCode}, accountSetId = #{accountSetId}, tallyPeriod = #{tallyPeriod}, tallyState = #{tallyState}, reimbursePeriod = #{reimbursePeriod}, reimburseState = #{reimburseState}, mnemonicCode = #{mnemonicCode}, updateTime = SYSDATE(), updateUserCode = #{inputUserCode}, customerAccountInfoId = #{customerAccountInfoId}
WHERE customerId = '${customerId}'

mapper.select:
	SELECT customerId, accountMonth,accountingSystemId,fixedasset

	FROM taxagency_customer_accountinfo  
 WHERE customerId = #{customerId}

	SELECT customerId id, customerName text  
	FROM taxagency_customer 
 WHERE organizationId = #{organizationId} 
 AND customerId != #{customerId} 
 AND customerState = '00'
